mapper.select:
select 
from comment
where status = 0
and entity_type = #{entityType}
and entity_id = #{entityId}
order by create_time asc
limit #{offset}, #{limit}

select 
from comment
where status = 0
and entity_type = #{entityType}
and user_id = #{entityId}
order by create_time asc
limit #{offset}, #{limit}
